type itemData = {
    /**
     * Item title text
     */
    title: string
    /**
     * Item price text
     */ 
    type: "CNC" | "3d Printed"
    /**
     * Item URL
     */
    url: string
    /**
     * Image source url
     */
    imageSrc: string
    /**
     * Item price text
     */ 
    price?: string
}